/* bn_sample.c */
#include <stdio.h>
#include <openssl/bn.h>
#define NBITS 256

void printBN(char *msg, BIGNUM *a)
{
    /* Use BN_bn2hex(a) for hex string
    * Use BN_bn2dec(a) for decimal string */
    char *number_str = BN_bn2hex(a);
    printf("%s %s\n", msg, number_str);
    OPENSSL_free(number_str);
}

int main()
{
    BN_CTX *ctx = BN_CTX_new();
    BIGNUM *n = BN_new();
    BIGNUM *m = BN_new();
    BIGNUM *e = BN_new();
    BIGNUM *s = BN_new();

    BN_hex2bn(&n, "");
    BN_hex2bn(&s, "");
    BN_hex2bn(&e, "10001");
    BN_hex2bn(&m, "d151c37125d9be7cde130ea3f0cc7f008f3f97b13b6aa438c87578e02ca957d5");

    // res= C = m^e  mod n
    BIGNUM *res = BN_new();
    BN_mod_exp(res, s, e, n, ctx);
    printBN("mË†e mod n = ", res);
    printBN("M = ", m);

    if (BN_ucmp(res, m) == 0)
        printf("Signature is valid.\n");
    else
        printf("Signature is not valid.\n");
    return 0;
}